package com.applicat.listapp

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.navigation.fragment.findNavController
import androidx.recyclerview.widget.LinearLayoutManager
import com.applicat.listapp.databinding.FragmentStartBinding


class Fragments : Fragment() {
    private lateinit var binding: FragmentStartBinding
    private val list = mutableListOf(
        FilmsModel(
            "https://upload.wikimedia.org/wikipedia/ru/thumb/2/2c/Alice_2016.jpg/640px-Alice_2016.jpg",
            "Alice in the Wonderland",
            "Fantasy / Adventure / Family",
            "Returning from Wonderland, Alice Kingsley decides to follow in her father's footsteps and spends several years sailing on the high seas. Upon returning from Asia back to London, the girl is faced with an insurmountable problem, which is that she absolutely does not understand what she should do for the rest of her life. Suddenly she sees a blue butterfly, which leads her to a strange mirror that leads to the Wonderland she knows."
        ), FilmsModel(
            "data:image/jpeg;base64,",
            "47 Ronin",
            "Fantasy / Action / Adventure",
            "The film takes place in ancient Japan, during the time of the samurai, who lived according to traditions and put honor above all else. One day, while in the forest, a man named Asano finds a little boy, Kai, and takes him under his wing. Years later, the boy turns into a strong man and warrior, a real samurai."
        ), FilmsModel(
            "https://avatars.mds.yandex.net/get-kinopoisk-image/1599028/5ab427c8-4a7d-4766-abf9-a9748a69dbf4/600x900",
            "John Wick",
            "Action / Thriller / Foreign",
            "The main character John Wick is a former assassin, a true professional in his field. He has more than a dozen victims, but now he has given up and is trying to lead a normal life. John lost his wife due to illness, and his only joy is a small puppy, which became a posthumous gift to his beloved. But one day an event occurs that forces him to take up arms again and resort to violence."
        )
    )


    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?
    ): View {
        binding = FragmentStartBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        binding.recyclers.setHasFixedSize(true)
        binding.recyclers.layoutManager = LinearLayoutManager(requireContext())
        binding.recyclers.adapter = CustomAdapter(
            requireActivity(), list
        ) {
            val action = FragmentsDirections.actionStartFragmentToDetailFragment2(list[it])
            findNavController().navigate(action)
        }
    }
}